def mint_from_many_wallets(contract):

    for w in range(5):
        pkey = os.getenv(f'DEV0{w}_PRIVATE_KEY')
        assert pkey is not None
        account = accounts.add(private_key=pkey)
        assert account.balance() > 0

        payable = Web3.toWei(amount, 'ether')

        contract.mint(mint_args, {
            "from": account,
            "value": payable,
            "priority_fee": "300 gwei",
            "base_fee": "20 wei"
        })

def main():
    print(f"Network: {network.show_active()}")

    contract = get_contract()

    while True:
        try:
            paused = contract.paused()
            if paused:
                print(f"[{round(time.time())}]: Still paused")
                time.sleep(1)
            else:
                print(f"Attempting to mint")
                mint_from_many_wallets(contract)
                break
        except Exception as e:
            print(e)

    print(f"DONE")
